Ext.define('cobuy.controller.ClosedOrderController', {
    extend: 'Ext.app.Controller',

    models: [
        'Order'
    ],
    stores: [
        'Orders'
    ],
    views: [
        'ClosedOrdersMainpanel',
        'TheClosedOrderWin'
    ],

    refs: [
        {
            ref: 'allClosedOrderGrid',
            selector: 'closedordersmainpanel grid'
        }
    ],

    onViewClosedOrdersButtonClick: function(button, e, eOpts) {
        var grid = this.getAllClosedOrderGrid(),
        		record = grid.getSelectionModel().getSelection();

        		if(record[0]) {
        			var editWin = Ext.create('cobuy.view.TheClosedOrderWin');
        			var form = editWin.down('form');


        			Ext.Ajax.request({

        				 url: '/cobuy/index.php?r=Order/retrieveOrderDetails',
        			     params: {
        					order_initiated_by: record[0].get('order_initiated_by'),
                             delivery_city_id:record[0].get('delivery_city_id'),
                             delivery_state_id:record[0].get('delivery_state_id'),
                             delivery_country_id:record[0].get('delivery_country_id'),
                             order_id:record[0].get('id')
        				},
                     //success or failure
        				success: function(response){
        				var jsonResponse = Ext.decode(response.responseText);
        					if (jsonResponse.success) {

                                var requester = jsonResponse.requester;
                                var country = jsonResponse.country;
                                var state = jsonResponse.state;
                                var city = jsonResponse.city;
                                 var payment_status=jsonResponse.payment_status;
                                var delivery_type=jsonResponse.delivery_type;



        					}

        					var values = {

                                requester:requester,
                                city:city,
                                state:state,
                                country:country,
                                payment_status:payment_status,
                                order_delivery_type:delivery_type


        					};


        					form.loadRecord(record[0]);

        					form.getForm().setValues(values);


        				}




        			});



        			editWin.show();
        		} else {
        				Ext.Msg.alert('Error!', 'Please Select an Order');
        		}
    },

    onViewThisClosedOrderButtonClick: function(button, e, eOpts) {
        var win = button.up('window');

        win.destroy();
    },

    onClosedOrderGridpanelItemDblClick: function(dataview, record, item, index, e, eOpts) {
        //this.onViewClosedOrdersButtonClick();

        var win = Ext.create('cobuy.view.ProductsInOrderWin');

        var values = {
            order_id:record.get('id'),
            status: record.get('status')
        };


        //load the form

        win.down('form').getForm().setValues(values);

        win.show();
    },

    onClosedOrderPanelRender: function(component, eOpts) {
        //var order_id = component.down('#orderidform').down('hiddenfield[name=order_id]').getValue();

        component.down('grid').getStore().load();
    },

    onProductsInOrderWindowRender: function(component, eOpts) {
        var order_id = component.down('#orderidform').down('hiddenfield[name=order_id]').getValue();

        //load the grid

        component.down('grid').getStore().load({
            params:{
                order_id:order_id
            }
        });
    },

    init: function(application) {
        this.control({
            "closedordersmainpanel button#viewBtn": {
                click: this.onViewClosedOrdersButtonClick
            },
            "theclosedorderwin button#viewBtn": {
                click: this.onViewThisClosedOrderButtonClick
            },
            "closedordersmainpanel grid": {
                itemdblclick: this.onClosedOrderGridpanelItemDblClick
            },
            "closedordersmainpanel": {
                render: this.onClosedOrderPanelRender
            },
            "productsinorderwin": {
                render: this.onProductsInOrderWindowRender
            }
        });
    }

});
